import React from 'react';
import {
  Card, CardBody, CardImg, CardTitle, CardSubtitle, CardText
} from 'reactstrap';
import Papa from 'papaparse'
import Logo from './restaurant.png';
import Csv from './restaurants.csv';

class ListRestaurants extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    }
    this.updateData = this.updateData.bind(this);
  }

  componentWillMount() {
    debugger
    const file = URL.createObjectURL(new File(['names'], Csv))
    Papa.parse(file, {
	delimiter: "",	// auto-detect
	newline: "",	// auto-detect
	quoteChar: '"',
	escapeChar: '"',
	header: false,
	transformHeader: undefined,
	dynamicTyping: false,
	preview: 0,
	encoding: "",
	worker: false,
	comments: false,
	step: undefined,
	complete: undefined,
	error: undefined,
	download: false,
	skipEmptyLines: false,
	chunk: undefined,
	fastMode: undefined,
	beforeFirstChunk: undefined,
	withCredentials: undefined,
	transform: undefined,
	delimitersToGuess: [',', '\t', '|', ';', Papa.RECORD_SEP, Papa.UNIT_SEP]
    });
  }

  updateData(updatedData) {
    console.log(updatedData);
    this.setState({ data: updatedData.data });
  }

  render() {
    return (
      <React.Fragment>
        <div className='page-header d-flex align-items-center'>
          <div className='mr-auto'>
            <h2>Restaurants</h2>
          </div>
        </div>
        <div className='page-content d-flex align-items-center'>
          <Card>
            <CardBody>
              <Card>
                <CardImg top width="100%" src="https://placeholdit.imgix.net/~text?txtsize=33&txt=318%C3%97180&w=318&h=180" alt="Card image cap" />
                <CardBody>
                  <CardTitle>Card title</CardTitle>
                  <CardSubtitle>Card subtitle</CardSubtitle>
                  <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                </CardBody>
              </Card>
            </CardBody>
          </Card>
        </div>
      </React.Fragment>
    )
  }
}

export default ListRestaurants;
