import React from 'react';
import {
  Card, CardBody, CardImg, CardTitle, CardSubtitle, CardText
} from 'reactstrap';
import Papa from 'papaparse'
import Logo from './restaurant.png';
import Csv from './restaurants.csv';

class ListRestaurants extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    }
    this.updateData = this.updateData.bind(this);
  }

  componentWillMount() {
    const file = `Name,City,Cuisine Style,Ranking,Rating,Number of Reviews
    Martine of Martine's Table,Amsterdam,"['French', 'Dutch', 'European']",1,5,136
    De Silveren Spiegel,Amsterdam,"['Dutch', 'European', 'Vegetarian Friendly', 'Gluten Free Options']",2,4.5,812
    La Rive,Amsterdam,"['Mediterranean', 'French', 'International', 'European', 'Vegetarian Friendly', 'Vegan Options']",3,4.5,567
    Vinkeles,Amsterdam,"['French', 'European', 'International', 'Contemporary', 'Vegetarian Friendly', 'Vegan Options', 'Gluten Free Options']",4,5,564
    Librije's Zusje Amsterdam,Amsterdam,"['Dutch', 'European', 'International', 'Vegetarian Friendly', 'Vegan Options', 'Gluten Free Options']",5,4.5,316
    Ciel Bleu Restaurant,Amsterdam,"['Contemporary', 'International', 'Vegetarian Friendly', 'Vegan Options', 'Gluten Free Options']",6,4.5,745
    Zaza's,Amsterdam,"['French', 'International', 'Mediterranean', 'European', 'Vegetarian Friendly', 'Vegan Options', 'Gluten Free Options']",7,4.5,1455
    Blue Pepper Restaurant And Candlelight Cruises,Amsterdam,"['Asian', 'Indonesian', 'Vegetarian Friendly', 'Vegan Options', 'Gluten Free Options']",8,4.5,675
    Teppanyaki Restaurant Sazanka,Amsterdam,"['Japanese', 'Asian', 'Vegetarian Friendly', 'Vegan Options', 'Gluten Free Options']",9,4.5,923
    Rob Wigboldus Vishandel,Amsterdam,"['Dutch', 'Seafood', 'Fast Food']",10,4.5,450
    The Happy Bull,Amsterdam,"['American', 'Bar', 'European', 'Vegetarian Friendly', 'Gluten Free Options']",11,4.5,295
    Gartine,Amsterdam,"['French', 'Dutch', 'International', 'European', 'Vegetarian Friendly', 'Vegan Options', 'Gluten Free Options']",12,4.5,967
    Restaurant Adam,Amsterdam,"['French', 'European', 'Central European', 'Vegetarian Friendly', 'Vegan Options', 'Gluten Free Options']",13,4.5,368
    Biercafe Gollem,Amsterdam,"['Bar', 'Pub']",14,4.5,586`
    Papa.parse(file, {
      delimiter: ',',
      preview: 0,
      complete: this.updateData,
      header: false,
      encoding: 'default',
    });
  }

  updateData(updatedData) {
    console.log(updatedData);
    this.setState({ data: updatedData.data });
  }

  render() {
    return (
      <React.Fragment>
        <div className='page-header d-flex align-items-center'>
          <div className='mr-auto'>
            <h2>Restaurants</h2>
          </div>
        </div>
        <div className='page-content d-flex align-items-center'>
          <Card>
            <CardBody>
              <Card>
                <CardImg top width="100%" src="https://placeholdit.imgix.net/~text?txtsize=33&txt=318%C3%97180&w=318&h=180" alt="Card image cap" />
                <CardBody>
                  <CardTitle>Card title</CardTitle>
                  <CardSubtitle>Card subtitle</CardSubtitle>
                  <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                </CardBody>
              </Card>
            </CardBody>
          </Card>
        </div>
      </React.Fragment>
    )
  }
}

export default ListRestaurants;
