import React from 'react';
import {
  Card, CardBody, CardImg, CardTitle, CardSubtitle, CardText
} from 'reactstrap';
import Papa from 'papaparse'
import Logo from './restaurant.png';
import Icon from './restaurant_1.png'
import { csvString } from './restaurants.js';

class ListRestaurants extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    }
    this.updateData = this.updateData.bind(this);
  }

  componentWillMount() {
    Papa.parse(csvString, {
      delimiter: ',',
      preview: 0,
      complete: this.updateData,
      header: false,
      encoding: 'default',
    });
  }

  updateData(updatedData) {
    this.setState({ data: updatedData.data });
  }

  render() {
    const { data } = this.state;
    const filteredData = data.slice(0, 20)
    return (
      <React.Fragment>
        <div className='page-header d-flex align-items-center'>
          <div className='mr-auto'>
            <img  src={Logo} alt='loading...' className='img'/>
            <h3>Restaurants</h3>
          </div>
        </div>
        <div className='page-content d-flex align-items-center'>
          <Card>
            <CardBody>

            </CardBody>
          </Card>
        </div>
      </React.Fragment>
    )
  }
}

export default ListRestaurants;
